import {useRef} from "react";
import { useState } from "react";

export default function FormRegistration ({onClick}){

    const [user, setUser] = useState({
        login: "",
        password: "",
        confirmPassword: "",
        email: "",
    })

    const handleChange = (e) => {
        const { name, value } = e.target;
        setUser(prevUser => ({
            ...prevUser,
            [name]: value
        }));
    }

    function printUser(){
        console.log(user);
    }

    let inputRef = useRef(null);
    let inputRef2 = useRef(null);

    return <form action="#" method="post" className="main__form form">
        <ul className="form__input-list auth-input-list">
        <li className="auth-input-list__item">
            <input type='email' value={user.email} onChange={handleChange} required placeholder='Email' className='form__userdata'/>
        </li>
        <li className="auth-input-list__item">
            <input type="text" placeholder="Логин" value={user.login} onChange={handleChange} className="form__login form__userdata"/>
        </li>
        </ul>
        <input id="before" type="password" value={user.password} onChange={handleChange} placeholder="Пароль" className="form__password form__userdata"/>
        <label htmlFor="before" className='input-label ' ref={inputRef} onClick={() => onClick(inputRef)}/>

        <input id="onemore-before" type="password" value={user.confirmPassword} onChange={handleChange} placeholder="Повторите пароль"
                className="form__password form__userdata"/>
        <label htmlFor="onemore-before" className='input-label' ref={inputRef2} onClick={() => onClick(inputRef2)}/>
        <button type='submit' onClick={printUser} className="form__button">Зарегистроваться</button>
    </form>
}
