import React from 'react';
import FormAuthorization from './FormAuthorization';
import FormRegistration from './FormRegistration';
import BeginPageBottom from './BeginPageBottom';
import '../../modules/scss/authorization.scss';
import { useState } from 'react';

export default function Authorization({page}) {

    const [isRegister, setIsRegister] = useState(page.isRegisterPage)

    function showPassword(ref){
        const tagId = ref.current.getAttribute('for')
        const inputPassword = document.getElementById(`${tagId}`);
    
        if (inputPassword.type === 'password') inputPassword.type = `text`;
        else inputPassword.type = 'password'
    }

    function ChangePages() {
        setIsRegister(!isRegister);
    }

    return(
    <main className='page-main'>
        <section className="main">
            <h1 className="main__title">{page.titleText}</h1>

            {this.isRegister && <FormRegistration onClick={showPassword} />}
            {!this.isRegister && <FormAuthorization onClick={showPassword}/>}

            <BeginPageBottom onChangePage={ChangePages} signText={page.signText}/>
        </section>
    </main>
    );
}
