import { useState } from "react"

export default function FormProfile(onUpdateUser){

  const [user, setUser] = useState({
    // Пока id=1, потом поменяю
    id: 1,
    login: "",
    lastname: "",
    email: ""
  })

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUser(prevUser => ({
        ...prevUser,
        [name]: value
    }));
}

  function updateUser() {
    onUpdateUser(user)
  }

  return <ul className="profile__input-list">
    <li className="profile__input-list__item">
      <input type="text" name="login" onChange={handleChange} className="profile__input-list__input name-input" placeholder='Логин'/>
    </li>
    <li className="input-list__item">
      <input type="text" name="lastname" onChange={handleChange} className="profile__input-list__input surname-input" placeholder='Полное имя'/>
    </li>
    <li className="input-list__item">
      <input type="email" name="email" onChange={handleChange} className="profile__input-list__input email-input" placeholder='Email'/>
    </li>
    {user.login !== "" && user.email !== "" (
      <button type='button' onClick={updateUser} className="form__button">Сохранить изменения</button>
    )}
  </ul>
}
