import {useRef, useState} from "react";

export default function FormAuthorization({onClick}){
  let inputRef = useRef(null);

  const [errorMessage, setErrorMessage] = useState('');
  const [loginData, setLoginData] = useState({
    email: "",
    password: ""
  })

  const handleChange = (e) => {
    const { name, value } = e.target;
    setLoginData(prevUser => ({
        ...prevUser,
        [name]: value
    }));
  }

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (loginData.password !== "" && loginData.email !== "") {
      const [errorMessage, setErrorMessage] = useState('');
        return;
    } else {
        setErrorMessage('');
    }
    onRegistrationSuccess();

    toast.success("Пользователь зарегистрирован", {
        position: "top-center",
    });

    try {
        const response = await axios.post("http://localhost:9090/api/user/register", {
            email: user.email,
            lastname: "",
            name: user.login,
            password: user.password,
            phoneNumber: "",
            userRole: 1
        });
        console.log("Пользователь успешно зарегистрирован: ", response.data);
        // onRegistrationSuccess();
    } catch (err) {
        console.error("Ошибка при регистрации пользователя: ",err);
    }

    setUser({
        login: '',
        password: '',
        confirmPassword: '',
        email: ''
    });
    navigate(-1);
  }

  return <form action="#" method="post" className="main__form form">
      <input type="text" name="email" onChange={handleChange} placeholder="Email" className="form__login form__userdata"/>
      <input id="before" name="password" onChange={handleChange} type="password" placeholder="Пароль" className="form__password form__userdata"/>
      <label htmlFor="before" className='input-lable' ref = {inputRef} onClick={() => onClick(inputRef)}></label>
    <button type='submit' className="form__button">Войти</button>
  </form>
}
