import React from 'react';
import BeginPageBottom from './BeginPageBottom';
import '../../modules/scss/authorization.scss';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Outlet } from 'react-router-dom';
import { useEffect } from 'react';


export default function Authorization({page, isAuth}) {

    const [isRegister, setIsRegister] = useState(() => {
        const storedIsRegister = localStorage.getItem('isRegister');
        return storedIsRegister ? JSON.parse(storedIsRegister) : isAuth;
    });
    
    const navigate = useNavigate();

    useEffect(() => {
        // При монтировании компонента устанавливаем слушатель события popstate,
        // чтобы обновлять значение isRegister при использовании кнопки "Назад" браузера
        const handlePopstate = () => {
            const storedIsRegister = localStorage.getItem('isRegister');
            setIsRegister(storedIsRegister ? JSON.parse(storedIsRegister) : false);
        };
        window.addEventListener('popstate', handlePopstate);
        return () => {
            window.removeEventListener('popstate', handlePopstate);
        };
    }, []);

    function ChangePages() {
        setIsRegister(!isRegister);
        if (!isRegister) {
            navigate('register');
        } else {
            navigate('login');
        }
    }

    return(
    <main className='page-main'>
        <section className="main">
            <h1 className="main__title">{isRegister ? page.titleSignUpText : page.titleSignInText}</h1>

            {/* {isRegister ? <Link to="register">Зарегистрироваться</Link> : <Link to="login">Войти</Link>} */}
            <Outlet />
            {/* {isRegister ? <Navigate to="register"/> : <Navigate to="login"/>} */}
            {/* {!isRegister && <FormAuthorization onClick={showPassword}/>} */}

            <BeginPageBottom
                onChangePage={ChangePages}
                signText={!isRegister ? page.signInText : page.signUpText}
            />
        </section>
    </main>
    );
}
